import React from 'react';

export default class Input extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            first : '',
            last : '',
            addr : '',
            phone : '',
            email : ''
        }

    }
    handleFirst = (first) =>{
        // console.log(event.target.value);
        var v = first.target.value;
        this.setState({first :v})

    }

    handlelast = (last) =>{
        // console.log(event1.target.value);
        var v1 = last.target.value;
        this.setState({last :v1})

    }
    handlelast = (addr) =>{
        // console.log(event1.target.value);
        var v1 = addr.target.value;
        this.setState({last :v1})

    }
    handlelast = (phone) =>{
        // console.log(event1.target.value);
        var v1 = phone.target.value;
        this.setState({last :v1})

    }
    handlelast = (email) =>{
        // console.log(event1.target.value);
        var v1 = email.target.value;
        this.setState({last :v1})

    }

    render(){
        return (

            <div>
                <table>
                    <tr>
                        <td>
                            <label>Firstname</label>
                        </td>
                        <td>
                            <input type = "text" name ="first" placeholder="Firstname" onChange ={this.handleFirst}/><br/><br/>
                        </td>
                    </tr>>
                    <tr>
                        <td>
                        </td>
                        <td>
                        </td>
                    </tr>
                </table>
                <label>Lastname</label>

                <input type = "text" name ="last" placeholder="Lastname" onChange ={this.handlelast} /><br/><br/>
                <label>Addresse</label>

                <input type = "text" name ="add" placeholder="Lastname" onChange ={this.handlelast} /><br/><br/>
                <label>Phone</label>

                <input type = "text" name ="phone" placeholder="Lastname" onChange ={this.handlelast} /><br/><br/>
                <label>Email</label>

                <input type = "text" name ="email" placeholder="Lastname" onChange ={this.handlelast} /><br/><br/>


        <h1>I'm {this.state.first.charAt(0).toUpperCase()}{this.state.first.slice(1)}{' '}{this.state.last}</h1>


            </div>

        )
    }
}
