}

class App extends React.Component {

  constructor(props) {
      super(props);
      this.state = {
          name: 'react',
          data: []
      }
  }

  componentDidMount() {
      axios.get(`https://jsonplaceholder.typicode.com/todos`)
      .then((res) => {
          console.log(res);
          console.log(res.data);

          let data = res.data;

          this.setState({ data });
          console.log('state data', this.state.data);
      })
      .catch((err) => {
          console.log(err);
      })
  }

  // handleOnChangeEvent = (event) => {
