import React from 'react';

export default class Input extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            first : '',
            last : '',
            addr : '',
            phone : '',
            email : ''
        }

    }
    handleFirst = (ev) =>{
        // console.log(event.target.value);
        var v = event.target.value;
        this.setState({first :v})

    }

    handlelast = (event1) =>{
        // console.log(event1.target.value);
        var v1 = event1.target.value;
        this.setState({last :v1})

    }

    render(){
        return (

            <div>
                <input type = "text" name ="first" placeholder="Firstname" onChange ={this.handleFirst}/><br/><br/>
                <input type = "text" name ="last" placeholder="Lastname" onChange ={this.handlelast} />
        <h1>I'm {this.state.first.charAt(0).toUpperCase()}{this.state.first.slice(1)}{' '}{this.state.last}</h1>


            </div>

        )
    }
}
